"""
Function to generate a complement to a nucleotide sequence (based on Watson-Crick pairing) and then reverse the string (to respect readin directions).
Filtering function to remove non-letter characters
"""

def CreateComplementSequence(sequence):
    sequence=sequence.upper()
    clean_seq=str().join(filter(str.isalpha, sequence))
    #print(clean_seq)
    complement=str()
    for i in range (len(clean_seq)):
         #print(clean_seq[i])
         if clean_seq[i] == 'A':
             complement=complement+('T')
         if clean_seq[i] == 'C':
             complement=complement+('G')
         if clean_seq[i] == 'G':
             complement=complement+('C')
         if clean_seq[i] == 'T':
             complement=complement+('A')
         else:
             pass
    #print(complement)
    return ReverseSequence(complement)
def ReverseSequence(seq):
    reverse=seq[::-1]
#slices with negative steps and so starts at the back and moves to start of original string
    return reverse

CreateComplementSequence('')
